/*!50003 DROP FUNCTION IF EXISTS `totalPenumpang` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `totalPenumpang`(ID INT) RETURNS int(11)
BEGIN
DECLARE total INT;
SELECT COALESCE(SUM(TRAVEL_TRANSACTION_PASSENGER),0) INTO total FROM TRAVEL_TRANSACTION WHERE TRAVEL_TRANSACTION.TRAVEL_SCHEDULE_ID=ID;
RETURN total;
    END */$$
DELIMITER ;

